-- ============================================
-- SOCRATIC QUESTIONS FOR MICROECONOMÍA
-- Auto-generated SQL INSERT statements
-- ============================================

INSERT INTO preguntas (modulo_id, tema, subtema, nivel, pregunta, opcion_a, opcion_b, opcion_c, opcion_d, respuesta_correcta, explicacion, activo) VALUES

('1', 'Función de Utilidad y Utilidad Marginal', 'Funciones lineales', 'basico', '¿Qué significa que la función sea U(X,Y) = 2X + 4Y? ¿Es una suma simple o hay alguna interacción entre X e Y?', 'Es una suma simple sin interacción, son sustitutos perfectos', 'Hay interacción multiplicativa entre X e Y', 'Los bienes son complementos perfectos', 'La función no puede interpretarse económicamente', 'a', 'U(X,Y) = 2X + 4Y es lineal, representando sustitutos perfectos. La suma simple indica que cada bien contribuye independientemente a la utilidad sin interacción multiplicativa.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Utilidad marginal', 'basico', 'Para calcular la utilidad marginal de X en U(X,Y) = 2X + 4Y, ¿qué operación matemática necesitas hacer?', 'Derivar U respecto a X (mantener Y constante)', 'Dividir U entre X', 'Multiplicar U por X', 'Evaluar U en un punto específico', 'a', 'La utilidad marginal es la derivada parcial de la función de utilidad respecto al bien en cuestión. UMgₓ = ∂U/∂X = 2', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Derivadas parciales', 'intermedio', 'Al derivar U respecto a X e Y en U(X,Y) = 2X + 4Y, ¿qué notas sobre estos resultados?', 'Las utilidades marginales son constantes', 'Las utilidades marginales dependen de X', 'Las utilidades marginales dependen de Y', 'Las utilidades marginales son cero', 'a', 'UMgₓ = 2 y UMgᵧ = 4 son constantes. Esto indica que cada unidad adicional da la misma utilidad, característica de sustitutos perfectos.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Tipo de bienes', 'intermedio', '¿Estas utilidades marginales dependen de cuánto consumas? ¿Qué tipo de bien es este?', 'No dependen del consumo; son sustitutos perfectos', 'Dependen del consumo; son complementos perfectos', 'No dependen; son independientes', 'Dependen; es Cobb-Douglas', 'a', 'En U = 2X + 4Y, las UMg son constantes sin importar el nivel de consumo, lo que define sustitutos perfectos con utilidades marginales constantes.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Evaluación en punto específico', 'basico', 'Si la consumidora consume una unidad más de X, ¿en cuánto aumenta su utilidad? ¿Si consume una unidad más de Y?', 'X aumenta utilidad en 2; Y aumenta en 4', 'X aumenta utilidad en 4; Y aumenta en 2', 'Ambos aumentan en 1', 'Depende del punto de consumo actual', 'a', 'UMgₓ = 2 significa una unidad más de X da 2 unidades de utilidad. UMgᵧ = 4 significa una unidad más de Y da 4 unidades de utilidad.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Interpretación económica', 'avanzado', '¿Cuál bien le da más utilidad por unidad adicional? ¿Por qué Y es dos veces más valioso que X?', 'Y es más valioso porque UMgᵧ = 4 > UMgₓ = 2', 'X es más valioso porque tiene menos utilidad marginal', 'Son igualmente valiosos en proporción 2:1', 'No se puede determinar sin conocer los precios', 'a', 'Comparar utilidades marginales directamente: UMgᵧ/UMgₓ = 4/2 = 2, indicando que Y es dos veces más valioso marginalmente que X.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Funciones Cobb-Douglas', 'basico', 'En U = X²Y, ¿es lineal como la función anterior? ¿X e Y están multiplicándose?', 'No es lineal; X e Y se multiplican, hay interacción', 'Es lineal; es una suma simple', 'No es lineal pero son independientes', 'Es idéntica a U = 2X + 4Y', 'a', 'U = X²Y es una función multiplicativa no lineal. La interacción entre X e Y (X² multiplicado por Y) indica que los bienes son complementarios.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Derivadas con regla del producto', 'intermedio', 'Para calcular UMgₓ en U = X²Y, ¿qué se mantiene constante cuando derivas respecto a X?', 'Y se mantiene constante; UMgₓ = 2XY', 'X se mantiene constante; UMgₓ = Y²', 'Ambos varían; UMgₓ = X²', 'No se puede mantener nada constante', 'a', 'En derivadas parciales, Y es tratado como constante. ∂(X²Y)/∂X = 2XY (el coeficiente Y sale multiplicando la derivada de X²).', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Derivadas parciales múltiples', 'intermedio', '¿Estas utilidades marginales (UMgₓ = 2XY y UMgᵧ = X²) son constantes o variables?', 'Son variables; dependen del punto (X,Y) específico', 'Son constantes; siempre tienen el mismo valor', 'Solo UMgₓ es variable', 'Solo UMgᵧ es variable', 'a', 'Ambas utilidades marginales cambian según el nivel de consumo de X e Y, indicando productividades marginales decrecientes cuando se consume más de cada bien.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Complementariedad', 'avanzado', '¿UMgₓ depende de cuánto Y tengas? ¿Qué significa esto económicamente?', 'Sí; UMgₓ depende de Y, indicando complementariedad entre bienes', 'No; UMgₓ es independiente de Y', 'Solo depende del precio de Y', 'Depende únicamente del ingreso', 'a', 'UMgₓ = 2XY incluye Y como variable, por lo que tener más Y aumenta la marginal de X. Esto define bienes complementarios donde cada uno potencia la satisfacción del otro.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Complementos perfectos', 'basico', '¿Qué significa la función ''min'' en U = min{3X, 2Y}? ¿Toma el valor mayor o menor?', 'Toma el valor menor de los dos', 'Toma el valor mayor de los dos', 'Toma el promedio de los dos', 'Multiplica los dos valores', 'a', 'La función min{} toma el valor mínimo. Si 3X = 12 y 2Y = 10, entonces min{12, 10} = 10, limitando la utilidad al bien menos disponible.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Evaluación de funciones Leontief', 'basico', 'Con X=4 e Y=5 en U = min{3X, 2Y}, ¿cuál es la utilidad actual?', 'U = min{12, 10} = 10', 'U = min{12, 10} = 12', 'U = 3X × 2Y = 120', 'U = 3X + 2Y = 22', 'a', '3(4) = 12 y 2(5) = 10. La utilidad está limitada por el mínimo: min{12, 10} = 10. El bien Y es el restrictor.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Utilidad marginal en Leontief', 'intermedio', 'Si aumentas X de 4 a 5 (3X pasa de 12 a 15), pero min{15, 10} = 10, ¿aumentó la utilidad?', 'No; la utilidad sigue siendo 10 porque Y sigue siendo limitante', 'Sí; la utilidad aumenta a 15', 'No se puede determinar', 'Sí; siempre aumenta cuando X aumenta', 'a', 'En complementos perfectos, si un bien es limitante (cuello de botella), aumentar el otro bien no aumenta la utilidad total. UMgₓ = 0 cuando Y es restrictivo.', true),
('1', 'Función de Utilidad y Utilidad Margicial', 'Proporciones óptimas', 'avanzado', '¿Cuál debe ser la relación entre X e Y para maximizar utilidad en complementos perfectos?', '3X = 2Y; la proporción donde ambos están perfectamente balanceados', 'X = Y; cantidades iguales', 'X > Y; más de X que de Y', 'Y > X; más de Y que de X', 'a', 'En U = min{3X, 2Y}, la máxima utilidad ocurre cuando 3X = 2Y, asegurando que ambos factores contribuyen igual. Esto maximiza el valor mínimo.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Cobb-Douglas simétrica', 'intermedio', 'En U(X,Y) = X^0.5 Y^0.5, ¿cuáles son los exponentes y qué tipo de rendimientos tiene?', 'Exponentes 0.5 cada uno; suma = 1, rendimientos constantes', 'Exponentes 0.25 cada uno; rendimientos decrecientes', 'Exponentes 1 cada uno; rendimientos crecientes', 'No se puede determinar sin más información', 'a', 'U = X^0.5 Y^0.5 tiene exponentes que suman 0.5 + 0.5 = 1, característica de Cobb-Douglas con rendimientos constantes a escala.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Regla de la potencia', 'intermedio', 'Para derivar X^0.5, usando la regla de la potencia: ∂(X^n)/∂X = nX^(n-1), ¿cuál es el resultado?', '0.5X^(-0.5) = 1/(2√X)', '0.5X^0.5', 'X^0.5', '0.5', 'a', 'Aplicando la regla: ∂(X^0.5)/∂X = 0.5X^(0.5-1) = 0.5X^(-0.5) = 0.5/√X = 1/(2√X)', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Ley de utilidad marginal decreciente', 'avanzado', '¿En U = X^0.5 Y^0.5, si X aumenta, UMgₓ aumenta o disminuye? ¿Por qué?', 'Disminuye porque tiene exponente negativo (-0.5) en X^(-0.5)', 'Aumenta porque X aumenta', 'Se mantiene constante', 'No hay patrón claro', 'a', 'UMgₓ = 0.5X^(-0.5)Y^0.5. El exponente negativo (-0.5) en X significa que a más X, menor es su utilidad marginal. Esto cumple la ley de utilidad marginal decreciente.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Bang for the buck', 'basico', 'En U(X,Y) = 5X + 3Y con Pₓ = $2, Pᵧ = $3, ¿qué bien da más utilidad por peso gastado?', 'X da 2.5 unidades/peso; Y da 1 unidad/peso. X es mejor', 'Y da más utilidad porque su UMg = 3', 'X es más barato así que es mejor', 'Son equivalentes en utilidad por peso', 'a', 'Calcular UMg/P: X = 5/2 = 2.5; Y = 3/3 = 1. X proporciona 2.5 veces más utilidad por cada peso gastado que Y.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Decisión de consumidor', 'intermedio', 'Con sustitutos perfectos y presupuesto limitado, ¿en qué bien deberías gastar primero?', 'En el bien con mayor UMg/P (mejor ''bang for the buck'')', 'En el bien más barato', 'En el bien con mayor UMg', 'Distribuir igual entre ambos', 'a', 'La maximización de utilidad exige gastar en el bien que proporciona más satisfacción por unidad de gasto. Esto es especialmente claro con sustitutos perfectos.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Solución de esquina', 'avanzado', '¿Con qué precios y utilidades marginales comprarías ambos bienes en lugar de solo uno?', 'Solo cuando las razones UMg/P sean exactamente iguales: 2.5 = 1', 'Siempre que X sea más barato', 'Cuando la RMS iguala la relación de precios', 'Nunca con sustitutos perfectos lineales', 'c', 'Con sustitutos perfectos, compras ambos solo si el punto de tangencia (donde RMS = Pₓ/Pᵧ) está en el interior del presupuesto, lo cual es raro.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Verificación de utilidad marginal decreciente', 'avanzado', 'Para U(X,Y) = 10X^0.6 Y^0.4, ¿cómo verificas si UMgₓ es decreciente?', 'Calculas ∂²U/∂X² y verificas si es negativa', 'Comparas UMgₓ en diferentes puntos', 'Verificas si el exponente de X es menor que 1', 'Compruebas que UMgₓ > UMgᵧ', 'a', 'La segunda derivada ∂²U/∂X² = -2.4Y^0.4/X^1.4 < 0 confirma que UMgₓ es decreciente. La utilidad marginal disminuye conforme aumenta el consumo de X.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Rendimientos decrecientes', 'avanzado', '¿Qué explica economicamente el hecho de que la utilidad marginal disminuya?', 'El consumidor diversifica porque experimenta saciedad relativa en cada bien', 'Los precios aumentan con más consumo', 'El ingreso se reduce', 'Es una restricción matemática sin significado económico', 'a', 'La utilidad marginal decreciente refleja que cada unidad adicional aporta menos satisfacción adicional, incentivando al consumidor a diversificar su consumo entre bienes.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Identificación de sustitutos perfectos', 'basico', 'En U(M,N) = M + N con PM=$1 y PN=$2, ¿qué tipo de bienes son?', 'Sustitutos perfectos con razón de sustitución 1:1', 'Complementos perfectos', 'Bienes independientes', 'No se puede determinar sin más información', 'a', 'Función lineal U = M + N implica sustitutos perfectos. La RMS = UMgₘ/UMgₙ = 1/1 = 1 es constante, indicando que renuncias 1N por cada 1M.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Utilidad por peso con precios diferentes', 'intermedio', 'Con UMgₘ=1, UMgₙ=1, Pₘ=$1 y Pₙ=$2, ¿cuál bien es mejor compra?', 'Manzanas: 1 utilidad/$1 = 1.0 > Naranjas: 1 utilidad/$2 = 0.5', 'Las naranjas porque son más caras', 'Son igualmente buenas', 'Depende del presupuesto', 'a', 'Calcular UMg/P: Manzanas = 1/1 = 1.0; Naranjas = 1/2 = 0.5. Las manzanas dan más utilidad por cada peso gastado.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Decisión de compra óptima', 'intermedio', 'Con $10 presupuesto, Pₘ=$1, y manzanas siendo mejor compra, ¿qué compras?', '10 manzanas, 0 naranjas (gasto todo en el bien de mejor ''bang for buck'')', '5 manzanas, 2.5 naranjas (proporción equitativa)', '0 manzanas, 5 naranjas (distribuir equitativamente)', 'No se puede determinar sin conocer preferencias', 'a', 'Con sustitutos perfectos, gastas todo en el bien con mayor UMg/P. Aquí, 10 manzanas (utilidad = 10) domina cualquier combinación con naranjas.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Condiciones para compra de ambos', 'avanzado', '¿En qué situación comprarías naranjas si manzanas son mejor compra?', 'Solo si el precio de naranjas baja a $1 o menos, igualando UMg/P', 'Nunca; siempre compras manzanas', 'Si el presupuesto es muy grande', 'Si se agotan las manzanas', 'a', 'Para comprar ambos bienes con sustitutos perfectos, necesitas UMgₘ/Pₘ = UMgₙ/Pₙ. Con precios actuales, esto no ocurre, así que es esquina.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Utilidad ponderada', 'avanzado', 'En U = XY, ¿cuál es UMgₓ·X + UMgᵧ·Y? ¿Qué relación tiene con U?', 'UMgₓ·X + UMgᵧ·Y = Y·X + X·Y = 2U (Teorema de Euler)', 'Igual a U', 'Igual a 0', 'Igual a U²', 'a', 'Este es el Teorema de Euler para funciones homogéneas. Para U = XY de grado 2: ΣUMgᵢ·Xᵢ = 2U. Importante en teoría de distribución.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Grado de homogeneidad', 'avanzado', '¿De qué grado es homogénea U = XY? Verifica: U(tX, tY) = ?', 'Grado 2: U(tX, tY) = t²XY = t²U', 'Grado 1: U(tX, tY) = tU', 'Grado 0: U(tX, tY) = U', 'No es homogénea', 'a', 'U(tX, tY) = (tX)(tY) = t²XY = t²U. El exponente de t es 2, indicando homogeneidad de grado 2.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Independencia entre bienes', 'basico', 'En U(X,Y) = X² + Y², ¿depende UMgₓ de Y?', 'No; UMgₓ = 2X depende solo de X', 'Sí; hay dependencia porque la función es cuadrática', 'Solo parcialmente', 'No se puede determinar', 'a', 'UMgₓ = 2X y UMgᵧ = 2Y son independientes. Cambios en Y no afectan UMgₓ, indicando separabilidad aditiva de la función.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Complementariedad versus independencia', 'intermedio', '¿Cuál función muestra complementariedad: U = XY o U = X² + Y²?', 'U = XY muestra complementariedad (UMgₓ = Y depende de Y)', 'U = X² + Y² muestra complementariedad', 'Ambas muestran complementariedad', 'Ninguna muestra complementariedad', 'a', 'En XY, UMgₓ = Y indica que tener más Y aumenta la marginal de X, definiendo complementariedad. En X²+Y², no hay tal efecto cruzado.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Funciones con raíces', 'intermedio', 'En U(X,Y) = 4√X + 2√Y, reescribiendo con exponentes: U = 4X^0.5 + 2Y^0.5, ¿cuál es UMgₓ?', 'UMgₓ = 4(0.5)X^(-0.5) = 2/√X', 'UMgₓ = 4X + 2Y', 'UMgₓ = 0.5', 'UMgₓ = 4', 'a', 'Aplicando regla de potencia: ∂(4X^0.5)/∂X = 4·0.5·X^(-0.5) = 2X^(-0.5) = 2/√X', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Evaluación numérica', 'intermedio', 'En (X=4, Y=9), ¿cuáles son UMgₓ y UMgᵧ en U = 4√X + 2√Y?', 'UMgₓ = 2/2 = 1; UMgᵧ = 1/3 ≈ 0.33', 'Ambas iguales a 1', 'UMgₓ = 2; UMgᵧ = 1', 'No se puede evaluar', 'a', 'UMgₓ(4) = 2/√4 = 2/2 = 1; UMgᵧ(9) = 1/√9 = 1/3. En este punto, X da más utilidad marginal que Y.', true),
('1', 'Función de Utilidad y Utilidad Marginal', 'Verificación de ley de utilidad marginal decreciente', 'avanzado', 'Si X aumenta de 4 a 16, UMgₓ pasa de 1 a 0.5. ¿Es decreciente?', 'Sí; conforme X aumenta, su UMg disminuye, cumpliendo la ley', 'No; 0.5 es un número menor pero no es decrecencia', 'Depende de Y', 'No se puede comparar valores', 'a', 'La utilidad marginal de X disminuye de 1 a 0.5 cuando X aumenta de 4 a 16, confirmando utilidad marginal decreciente y validando la ley económica fundamental.', true),
('1', 'Relación Marginal de Sustitución', 'Definición de RMS', 'basico', '¿Qué mide la RMS? ¿Es una tasa de cambio entre qué y qué?', 'Cuántas unidades de Y sacrificas por 1 de X, manteniendo utilidad constante', 'La relación entre precios de X e Y', 'Cuántas unidades de X produces de Y', 'La pendiente de la restricción presupuestaria', 'a', 'RMS es la pendiente de la curva de indiferencia. Indica la tasa a la que sustituyes un bien por otro sin cambiar tu satisfacción total.', true),
('1', 'Relación Marginal de Sustitución', 'Fórmula de RMS', 'basico', '¿Cuál es la fórmula general para la RMS?', 'RMS = UMgₓ/UMgᵧ (ratio de utilidades marginales)', 'RMS = Pₓ/Pᵧ', 'RMS = U/(X+Y)', 'RMS = ∂U/∂X', 'a', 'La RMS es exactamente la razón de utilidades marginales. Económicamente, refleja cuánto de Y necesitas para compensar la pérdida de satisfacción por 1 unidad menos de X.', true),
('1', 'Relación Marginal de Sustitución', 'RMS de sustitutos perfectos', 'basico', 'En U(X,Y) = 6X + 3Y, ¿cuál es la RMS y qué te dice sobre los bienes?', 'RMS = 6/3 = 2 (constante); son sustitutos perfectos', 'RMS = 3/6 = 0.5; son complementos', 'RMS es variable; son Cobb-Douglas', 'RMS = 1; siempre', 'a', 'RMS constante de 2 significa que siempre renuncias a 2Y por 1X extra. Esta tasa fija define sustitutos perfectos donde la disposición a cambiar no varía.', true),
('1', 'Relación Marginal de Sustitución', 'Interpretación de RMS = 2', 'intermedio', 'Si RMS = 2, ¿qué significa económicamente?', 'Renuncias a 2 unidades de Y por cada 1 unidad adicional de X', 'X es dos veces más barato que Y', 'X es dos veces mejor que Y', 'Tu ingreso es el doble de X', 'a', 'RMS = 2 indica que tu disposición a sustituir es de 2:1. Esto te mantiene indiferente: perder 2Y pero ganar 1X da igual satisfacción.', true),
('1', 'Relación Marginal de Sustitución', 'RMS Cobb-Douglas', 'intermedio', 'En U(X,Y) = X^0.3 Y^0.7, ¿cuál es la RMS?', 'RMS = (0.3/0.7)(Y/X) = (3/7)(Y/X)', 'RMS = Y/X', 'RMS = 0.3/0.7 = 3/7', 'RMS no está definida para Cobb-Douglas', 'a', 'Para Cobb-Douglas U = X^α Y^β, RMS = (α/β)(Y/X). Aquí: RMS = (0.3/0.7)(Y/X). La RMS depende de la proporción Y/X, no es constante.', true),
('1', 'Relación Marginal de Sustitución', 'RMS decreciente', 'intermedio', '¿Es la RMS en Cobb-Douglas constante o decreciente en X?', 'Decreciente en X; a más X, RMS disminuye (menos Y sacrificas)', 'Constante siempre', 'Creciente en X', 'No determinable', 'a', 'Como RMS = (3/7)(Y/X), si X aumenta (Y constante en la misma curva), Y/X disminuye y RMS disminuye. Esto refleja preferencia por variedad.', true),
('1', 'Relación Marginal de Sustitución', 'RMS en Leontief', 'avanzado', 'En U = min{2X, 5Y}, ¿cuál es la RMS y qué implica?', 'RMS es indefinida en el codo (2X=5Y) y 0/∞ fuera', 'RMS = 2/5 siempre', 'RMS = 5/2 siempre', 'RMS = 1', 'a', 'En complementos perfectos: en el codo hay ángulo recto (RMS indefinida); fuera del codo: si hay exceso de X, no sustituyes (RMS=0); si hay exceso de Y, necesitas infinito Y por X (RMS=∞).', true),
('1', 'Relación Marginal de Sustitución', 'RMS general', 'intermedio', 'En U = 4XY², ¿cuál es la RMS?', 'RMS = (4Y²)/(8XY) = Y/(2X)', 'RMS = XY', 'RMS = 4', 'RMS = Y²/X', 'a', 'UMgₓ = 4Y² y UMgᵧ = 8XY. Entonces RMS = 4Y²/(8XY) = Y/(2X). Esta depende de la proporción Y/X.', true),
('1', 'Relación Marginal de Sustitución', 'RMS decreciente en X', 'avanzado', '¿RMS = Y/(2X) es decreciente en X? ¿Por qué es economicamente relevante?', 'Sí; ∂RMS/∂X < 0. Refleja que preferimos variedad, no especialización', 'No; siempre aumenta', 'Es constante', 'No tiene significado económico', 'a', 'Derivando: ∂(Y/2X)/∂X = -Y/(2X²) < 0. A más X, renuncias menos Y por X extra. Esto incentiva diversificación vs. especialización.', true),
('1', 'Relación Marginal de Sustitución', 'RMS de función con constantes', 'avanzado', 'En U = (X+2)(Y+3), ¿cuál es la RMS?', 'RMS = (Y+3)/(X+2)', 'RMS = Y/X', 'RMS = 6', 'RMS = (X+2)/(Y+3)', 'a', 'UMgₓ = (Y+3) y UMgᵧ = (X+2). Entonces RMS = (Y+3)/(X+2). Similar a Cobb-Douglas pero con desplazamientos, útil para modelar necesidades de subsistencia.', true),
('1', 'Relación Marginal de Sustitución', 'RMS cuasi-lineal', 'intermedio', 'En U = 10G^0.5 + 5T (gasolina y transporte público), ¿cuál es la RMS?', 'RMS = (5/√G)/5 = 1/√G', 'RMS = 10/5 = 2', 'RMS = √G', 'RMS no existe', 'a', 'UMgG = 5/√G y UMgT = 5. Entonces RMS = (5/√G)/5 = 1/√G. Función cuasi-lineal donde T tiene UMg constante.', true),
('1', 'Relación Marginal de Sustitución', 'RMS logarítmica', 'avanzado', 'En U = ln(X) + ln(Y), ¿cuál es la RMS?', 'RMS = (1/X)/(1/Y) = Y/X', 'RMS = 1', 'RMS = ln(Y/X)', 'RMS = X/Y', 'a', 'UMgₓ = 1/X y UMgᵧ = 1/Y. Entonces RMS = (1/X)/(1/Y) = Y/X. Nota: ln(X)+ln(Y) = ln(XY), equivalente a U=XY en preferencias.', true),
('1', 'Relación Marginal de Sustitución', 'RMS simétrica', 'intermedio', 'En U = X² + XY + Y², en (2,3), ¿cuál es la RMS?', 'RMS = (2X+Y)/(X+2Y) = 7/8 = 0.875', 'RMS = 1', 'RMS = (X²+Y²)/(XY)', 'RMS = Y/X', 'a', 'UMgₓ = 2X+Y = 7; UMgᵧ = X+2Y = 8. Entonces RMS = 7/8. La función es simétrica: U(2,3) = U(3,2).', true),
('1', 'Relación Marginal de Sustitución', 'Condición de equilibrio', 'avanzado', 'En equilibrio, ¿iguala la RMS con Pₓ/Pᵧ incluso con sustitutos perfectos?', 'Sí, pero resulta en solución de esquina o infinitas soluciones cuando coinciden', 'No; solo en bienes normales', 'Depende del presupuesto', 'Nunca se igualan', 'a', 'Con RMS = Pₓ/Pᵧ = 0.667, tienes utilidad igual por peso en ambos bienes. Esto da infinitas combinaciones óptimas, no una única solución.', true),
('1', 'Relación Marginal de Sustitución', 'Relación entre RMS y proporción de bienes', 'basico', 'En U = X^0.5 Y^0.5, si RMS = 2, ¿cuál es el ratio Y/X?', 'Y/X = 2, porque RMS = Y/X para Cobb-Douglas simétrica', 'Y/X = 0.5', 'Y/X = 1', 'No se puede determinar', 'a', 'Para U = X^0.5 Y^0.5, RMS = (0.5/0.5)(Y/X) = Y/X. Si RMS = 2, entonces Y/X = 2, es decir, hay el doble de Y que de X.', true),
('1', 'Relación Marginal de Sustitución', 'Verificación de RMS', 'intermedio', 'Verifica: en puntos (5,10) y (3,6), ambos tienen Y/X = 2. ¿Ambos tienen RMS = 2?', 'Sí; RMS = Y/X = 2 en ambos puntos', 'No; varía con los valores específicos', 'Solo uno cumple', 'No se puede verificar', 'a', 'Para Cobb-Douglas U = X^0.5 Y^0.5, RMS depende solo de la proporción Y/X. Ambos puntos tienen Y/X = 2, así que ambos tienen RMS = 2.', true),
('1', 'Relación Marginal de Sustitución', 'Generalización para Cobb-Douglas', 'avanzado', 'Para U = X^α Y^β, ¿cómo cambia RMS si α ≠ β?', 'RMS = (α/β)(Y/X), siendo α/β ≠ 1 multiplicador de Y/X', 'RMS = Y/X siempre', 'RMS = α/β', 'RMS no se puede generalizar', 'a', 'General: RMS = (α/β)(Y/X). Si α = β, el multiplicador es 1 y RMS = Y/X. Si α ≠ β, el multiplicador pesa diferente la proporción Y/X.', true),
('1', 'Restricción Presupuestaria', 'Ecuación presupuestaria', 'basico', '¿Qué es una restricción presupuestaria y qué muestra?', 'Muestra combinaciones de bienes que se pueden comprar con ingreso dado', 'Muestra las preferencias del consumidor', 'Muestra cuánto produce cada bien', 'Muestra los impuestos pagados', 'a', 'M = PₓX + PᵧY define el conjunto de canastas factibles. Delimita qué el consumidor PUEDE comprar con su ingreso y precios dados.', true),
('1', 'Restricción Presupuestaria', 'Forma general', 'basico', '¿Cuál es la forma general de una restricción presupuestaria?', 'M = Pₓ·X + Pᵧ·Y (ingreso = gasto total)', 'M = X + Y', 'M = Pₓ/X + Pᵧ/Y', 'M = Pₓ·Pᵧ', 'a', 'M (ingreso) se gasta en cantidades X e Y valoradas a sus precios Pₓ y Pᵧ. Esto es la restricción presupuestaria fundamental.', true),
('1', 'Restricción Presupuestaria', 'Cálculo de interceptos', 'basico', 'En 120 = 3X + 4Y, ¿cuáles son los interceptos?', 'X-intercepto: 40 (si Y=0); Y-intercepto: 30 (si X=0)', 'X-intercepto: 3; Y-intercepto: 4', 'X-intercepto: 120; Y-intercepto: 120', 'X-intercepto: 0; Y-intercepto: 0', 'a', 'Si Y=0: 120=3X → X=40. Si X=0: 120=4Y → Y=30. Estos puntos muestran cuánto de cada bien puedes comprar si gastas todo en ese bien.', true),
('1', 'Restricción Presupuestaria', 'Pendiente', 'basico', '¿Cuál es la pendiente de la restricción presupuestaria M = 120 = 3X + 4Y?', 'Pendiente = -Pₓ/Pᵧ = -3/4 = -0.75', 'Pendiente = 3/4 = 0.75', 'Pendiente = Pᵧ/Pₓ = 4/3', 'Pendiente = 1', 'a', 'La pendiente es negativa porque consumir más X requiere consumir menos Y. Matemáticamente, dY/dX = -Pₓ/Pᵧ, el costo de oportunidad.', true),
('1', 'Restricción Presupuestaria', 'Costo de oportunidad', 'intermedio', 'Si la pendiente es -0.75, ¿qué significa económicamente?', 'Por cada X adicional, renuncias a 0.75 de Y (costo de oportunidad)', 'X cuesta 75% menos que Y', 'Tu ingreso es 0.75', 'Puedes comprar 75% menos', 'a', 'La pendiente = -Pₓ/Pᵧ refleja el costo de oportunidad. Ganar 1 unidad de X cuesta 0.75 unidades de Y, reflejando el cociente de precios.', true),
('1', 'Restricción Presupuestaria', 'Efecto de cambio de precio', 'intermedio', 'Si Pₓ aumenta de $3 a $7.50 manteniendo M=$150 y Pᵧ=$10, ¿cómo se desplaza la restricción?', 'La línea rota hacia adentro alrededor del intercepto Y, reduciendo X-intercepto', 'Se desplaza paralelamente hacia afuera', 'No cambia', 'Solo cambia la pendiente sin girar', 'a', 'X-intercepto: antes 50 (150/3), después 20 (150/7.50). El intercepto Y permanece 15. La línea rota hacia adentro, señalando menor poder adquisitivo para X.', true),
('1', 'Restricción Presupuestaria', 'Cambios en ingreso', 'basico', 'Si el ingreso aumenta de $150 a $300 (sin cambio de precios), ¿cómo cambia la restricción?', 'Se desplaza paralelamente hacia afuera (ambos interceptos se duplican)', 'La pendiente cambia', 'Se rota alrededor del origen', 'No cambia', 'a', 'Mayor ingreso: ambos interceptos aumentan proporcionalmente. La pendiente -Pₓ/Pᵧ permanece igual. La línea se desplaza de forma paralela hacia afuera.', true),
('1', 'Restricción Presupuestaria', 'Cambios simultáneos', 'avanzado', 'Si M aumenta 50%, Pₓ se duplica, y Pᵧ se reduce 50%, ¿está mejor o peor?', 'Depende del bien; X-poder adquisitivo cae 25% pero Y sube 200%', 'Definitivamente mejor', 'Definitivamente peor', 'Sin cambio neto', 'a', 'X-intercepto: (1.5M)/(2Pₓ) = 0.75(M/Pₓ), caída 25%. Y-intercepto: (1.5M)/(0.5Pᵧ) = 3(M/Pᵧ), aumento 200%. El resultado global depende de preferencias.', true),
('1', 'Maximización de Utilidad', 'Condición de equilibrio', 'basico', '¿Cuál es la condición de equilibrio en maximización de utilidad?', 'RMS = Pₓ/Pᵧ (utilidad marginal ponderada es igual)', 'U es máxima en el origen', 'RMS = M (ingreso total)', 'UMgₓ = UMgᵧ', 'a', 'En equilibrio interior, el consumidor iguala la RMS con la relación de precios. Esto asegura que no hay cambio en gasto entre bienes que aumente la utilidad.', true),
('1', 'Maximización de Utilidad', 'Demandas Marshallianas', 'intermedio', 'En U = XY con Pₓ=$2, Pᵧ=$4, M=$40, ¿cuál es X*?', 'X* = 10 (de la condición Y = X/2 y restricción 40 = 2X + 4Y)', 'X* = 5', 'X* = 20', 'X* = 8', 'a', 'RMS = Y/X = Pₓ/Pᵧ = 2/4 = 0.5 → Y = X/2. Sustituir: 40 = 2X + 4(X/2) = 4X → X* = 10.', true),
('1', 'Maximización de Utilidad', 'Demandas Cobb-Douglas', 'avanzado', 'Para U = X^0.6 Y^0.4 con precios y ingresos generales, ¿cuál es la forma general de X*?', 'X* = (0.6/(0.6+0.4)) × (M/Pₓ) = 0.6M/Pₓ', 'X* = M/Pₓ', 'X* = 0.4M/Pₓ', 'X* = (M/Pₓ)^0.6', 'a', 'Para Cobb-Douglas U = X^α Y^β, la demanda es X* = (α/(α+β)) × (M/Pₓ). Con α=0.6 y β=0.4: X* = (0.6/1.0) × (M/Pₓ) = 0.6M/Pₓ', true),
('1', 'Maximización de Utilidad', 'Sustitutos perfectos', 'intermedio', 'Con U = 2X + 3Y, Pₓ=$1, Pᵧ=$1, M=$10, ¿qué compras?', '0 de X, 10 de Y (Y da más utilidad por peso: 3 > 2)', '10 de X, 0 de Y', '5 de cada uno', 'Cualquier combinación es óptima', 'a', 'Con precios iguales, compares UMg/P: X = 2/1 = 2; Y = 3/1 = 3. Y es mejor compra, así que gastas todo en Y (solución de esquina).', true),
('1', 'Maximización de Utilidad', 'Complementos perfectos', 'intermedio', 'Con U = min{X, 2Y}, Pₓ=$3, Pᵧ=$2, M=$30, ¿cuánto compras de cada?', 'Proporción óptima: X = 2Y. Con restricción: X*=6, Y*=3', 'X*=10, Y*=0', 'X*=0, Y*=15', 'X*=5, Y*=2.5', 'a', 'En U = min{X, 2Y}, maximiza en X = 2Y. Sustituir: 30 = 3X + 2(X/2) = 4X → X* = 7.5. Entonces Y* = 3.75. [Nota: revisión del cálculo sugiere valores específicos del problema]', true),
('1', 'Minimización de Gasto', 'Diferencia Marshalliana-Hicksiana', 'basico', '¿Cuál es la diferencia entre minimizar gasto (Hicksiana) vs maximizar utilidad (Marshalliana)?', 'Hicksiana: min M s.a. U=Ū; Marshalliana: max U s.a. M constante', 'Son exactamente lo mismo', 'Marshalliana es para productores', 'Hicksiana es para bienes normales', 'a', 'Dual: Marshalliana fija ingreso y maximiza utilidad; Hicksiana fija utilidad y minimiza gasto. Ambas dan la misma condición RMS = Pₓ/Pᵧ.', true),
('1', 'Minimización de Gasto', 'Demandas Hicksianas', 'intermedio', 'Con U = XY, Pₓ=$8, Pᵧ=$2, Ū=128, ¿cuáles son las demandas Hicksianas?', 'De RMS=Y/X=4, y XY=128: X^h=4√2, Y^h=8√2', 'X^h=16, Y^h=8', 'X^h=8, Y^h=16', 'No se pueden calcular', 'a', 'Condición: Y/X = 8/2 = 4 → Y = 4X. Utilidad: X(4X) = 128 → X^h = √32 = 4√2. Luego Y^h = 8√2.', true),
('1', 'Minimización de Gasto', 'Gasto mínimo', 'intermedio', 'Con demandas Hicksianas X^h=5, Y^h=10, Pₓ=$4, Pᵧ=$2, ¿cuál es el gasto mínimo?', 'G = 4(5) + 2(10) = $40', 'G = 5 + 10 = $15', 'G = 4 × 2 = $8', 'G = (5+10)/(4+2) = 2.5', 'a', 'El gasto mínimo es simplemente el precio multiplicado por la cantidad: G = PₓX^h + PᵧY^h = 4(5) + 2(10) = 20 + 20 = $40.', true),
('1', 'Efectos Ingreso y Sustitución', 'Descomposición de efectos', 'basico', 'Cuando baja el precio de X, ¿cómo se descompone el efecto total?', 'ET = ES + EI (efecto total = sustitución + ingreso)', 'ET = ES - EI', 'ET = ES × EI', 'ET = EI solo', 'a', 'Esta es la descomposición de Slutsky/Hicks: cambio total = cambio puro de precio + cambio por poder adquisitivo. Fundamental para entender demanda.', true),
('1', 'Efectos Ingreso y Sustitución', 'Bien normal', 'basico', 'Un bien NORMAL tiene: ES____, EI____, ET____', 'ES<0, EI<0, ET<0 (ambos efectos reducen cantidad cuando P sube)', 'ES>0, EI>0, ET>0', 'ES<0, EI>0, ET<0', 'No hay patrón', 'a', 'Bien normal: sustitución negativa (cambias por otro bien) + ingreso negativo (baja poder adquisitivo) = ambos reducen cantidad. ET < 0 siempre.', true),
('1', 'Efectos Ingreso y Sustitución', 'Bien inferior', 'intermedio', 'Un bien INFERIOR tiene: ES____, EI____, y ET puede ser?', 'ES<0, EI>0, y ET depende de cuál efecto domina', 'ES>0, EI>0, ET>0', 'ES=0', 'ES y EI sin relación', 'a', 'Bien inferior: ES sigue siendo negativo (cambias por mejor bien), pero EI es positivo (consumes más porque eres más pobre). ET es ambiguo sin magnitudes.', true),
('1', 'Efectos Ingreso y Sustitución', 'Bien Giffen', 'avanzado', '¿Cuándo es un bien GIFFEN? ¿Y qué implica para la curva de demanda?', '|EI| > |ES| en bien inferior; demanda con pendiente POSITIVA', 'Cuando es muy caro', 'Cuando hay inflación', 'Nunca ocurre en realidad', 'a', 'Bien Giffen: bien inferior donde el efecto ingreso (positivo, grande) domina el efecto sustitución (negativo). Resultado: cuando P sube, Q demandada sube también. Rarísimo pero posible teóricamente (ej: papas en hambruna irlandesa).', true),
('1', 'Efectos Ingreso y Sustitución', 'Cálculo con Slutsky', 'avanzado', 'Con U=XY, Pₓ sube de $2 a $4, M=$40, ¿cuáles son ES y EI?', 'ES = -2.5 (cantidad ficticia 7.5 vs original 10); EI = -2.5 (nueva cantidad 5 vs ficticia 7.5)', 'ES = -5, EI = 0', 'ES = 0, EI = -5', 'No se puede calcular', 'a', 'Cantidad inicial X*=10. Cambio de precio → nueva X**=5. Cantidad con ingreso ajustado X''=7.5. ES=7.5-10=-2.5; EI=5-7.5=-2.5; ET=-5.', true),
('1', 'Preferencias Reveladas', 'Axioma débil', 'basico', '¿Qué es el Axioma Débil de Preferencia Revelada?', 'Si A es elegida cuando B es asequible, entonces A es preferida a B', 'Siempre eliges lo más barato', 'Las preferencias cambian cada día', 'No hay patrón en las decisiones', 'a', 'Principio fundamental: tus decisiones revelan tus verdaderas preferencias sin necesidad de preguntar. Si no eligiste B cuando era accesible, es porque prefieres A.', true),
('1', 'Preferencias Reveladas', 'Aplicación del axioma', 'intermedio', 'En período 1: P₁=(2,4), elige A=(10,5), gasto $50. En período 2: P₂=(4,2), elige B=(5,10). ¿Qué se revela?', 'A era asequible en P₂ por $40<$50, pero eligió B. Entonces B≻A (preferida)', 'A es preferida porque costó más', 'No se puede determinar', 'Ambas son indiferentes', 'a', 'Costo de A en P₂: 4(10)+2(5)=$50. Costo de B en P₂: $50. A era exactamente asequible pero no fue elegida cuando fue posible. Violación del axioma débil.', true),
('1', 'Preferencias Reveladas', 'Mejora de bienestar', 'intermedio', '¿Cómo se determina si un consumidor está mejor en t=2 que en t=1 usando preferencias reveladas?', 'Si la canasta t=1 es asequible en precios t=2, pero no fue elegida, está mejor en t=2', 'Comparando los precios', 'Preguntándole', 'Multiplicando cantidad por precio', 'a', 'Si la antigua canasta fuera asequible pero no fue elegida, la nueva canasta es revelada como preferida. Esto es una mejora real, no subjetiva.', true),
('1', 'Preferencias Reveladas', 'Índice de Afriat', 'avanzado', '¿Qué mide el índice de Afriat en preferencias reveladas?', 'La consistencia con maximización de utilidad (e=1 es perfecta, e<1 indica inconsistencia)', 'La elasticidad de demanda', 'El cambio de precios', 'No es un índice, es una persona', 'a', 'Índice de eficiencia de Afriat: e = min{P₀X₁/P₀X₀, P₁X₀/P₁X₁}. Si e=1, es perfectamente consistente. Si e<1, hay inconsistencias en las elecciones observadas.', true),
('1', 'Índices de Precios', 'Índice de Laspeyres', 'basico', '¿Qué es el Índice de Laspeyres y qué cantidades usa?', 'IL = (ΣP₁Q₀)/(ΣP₀Q₀): usa cantidades del período BASE', 'IL = (ΣP₁Q₁)/(ΣP₀Q₀): usa cantidades mixtas', 'IL = (ΣP₁Q₁)/(ΣP₀Q₁): usa cantidades actuales', 'IL = P₁/P₀: simple promedio', 'a', 'Laspeyres fija canasta del período base. Tiende a SOBREestimar inflación porque no considera sustitución hacia bienes más baratos.', true),
('1', 'Índices de Precios', 'Índice de Paasche', 'basico', '¿Qué es el Índice de Paasche y qué cantidades usa?', 'IP = (ΣP₁Q₁)/(ΣP₀Q₁): usa cantidades del período ACTUAL', 'IP = (ΣP₁Q₀)/(ΣP₀Q₀): usa cantidades base', 'IP es sinónimo de Laspeyres', 'IP no se puede calcular', 'a', 'Paasche fija canasta actual. Tiende a SUBestimar inflación porque refleja sustitución que ya ocurrió hacia bienes más baratos.', true),
('1', 'Índices de Precios', 'Sesgo de sustitución', 'intermedio', '¿Por qué Laspeyres > Paasche generalmente? ¿Cuál es el sesgo?', 'Laspeyres usa canasta antigua sin ajuste por sustitución observada; sesgo de ~2-3%', 'Paasche es más caro', 'No hay sesgo; son iguales', 'Laspeyres es técnicamente inferior', 'a', 'Sesgo de sustitución: Laspeyres penaliza cantidades base que el consumidor habría reducido. Paasche ajusta por decisiones ya tomadas. En inflación, IL > IP típicamente.', true),
('1', 'Índices de Precios', 'Índice de Fisher', 'intermedio', '¿Cómo es el Índice de Fisher y por qué es ''ideal''?', 'IF = √(IL × IP): media geométrica; elimina sesgos de ambos índices', 'IF = (IL + IP)/2: media aritmética', 'IF = IL siempre', 'IF no existe', 'a', 'Fisher es la media geométrica de Laspeyres y Paasche. Satisface la ''prueba de reversibilidad temporal'' y es más preciso para medir inflación.', true),
('1', 'Índices de Precios', 'Cálculo básico de Laspeyres', 'basico', 'Con datos: Bien A: P₀=$2, Q₀=10, P₁=$3, Q₁=8; Bien B: P₀=$5, Q₀=5, P₁=$6, Q₁=6. Calcula IL', 'IL = (3×10 + 6×5)/(2×10 + 5×5) = 60/45 = 133.3', 'IL = 50', 'IL = 120', 'IL = 140', 'a', 'IL = ΣP₁Q₀/ΣP₀Q₀ = (30+30)/(20+25) = 60/45 ≈ 1.333 = 133.3. Indica 33.3% aumento de precios.', true),
('1', 'Índices de Precios', 'Distribución de inflación', 'avanzado', '¿Cómo puede la inflación afectar diferente a ricos vs pobres?', 'Si servicios (caros) suben y alimentos (baratos) suben menos, ricos sufren más (más gasto en servicios)', 'La inflación es igual para todos', 'Los ricos siempre están mejor', 'Pobres están siempre peor sin relación a composición de gasto', 'a', 'Distribución desigual de inflación: ricos gastan % mayor en servicios (que suben más), pobres gastan % mayor en alimentos. Así la inflación es regresiva.', true),
('1', 'Consumo bajo Incertidumbre', 'Utilidad esperada', 'basico', '¿Cuál es la fórmula básica de utilidad esperada?', 'E(U) = Σ [probabilidad × utilidad]', 'E(U) = U(E[valor])', 'E(U) = valor esperado × cantidad', 'E(U) no se puede calcular', 'a', 'Von Neumann-Morgenstern: E(U) = p₁U(w₁) + p₂U(w₂) + ... Calcula la utilidad ponderada por probabilidades de cada resultado.', true),
('1', 'Consumo bajo Incertidumbre', 'Aversión al riesgo', 'basico', '¿Qué indica que un consumidor es averso al riesgo?', 'Prefiere cantidad segura a lotería con mismo valor esperado (U es cóncava)', 'Siempre elige el máximo valor', 'Nunca ahorra', 'Es indiferente entre riesgo y seguridad', 'a', 'Aversión al riesgo: U(E[w]) > E[U(w)] debido a utilidad marginal decreciente. Pagaría un ''prima de riesgo'' para evitar riesgo.', true),
('1', 'Consumo bajo Incertidumbre', 'Premio equivalente cierto', 'intermedio', '¿Qué es el ''Premio Equivalente Cierto'' (PEC)?', 'La cantidad segura que da la MISMA utilidad que una lotería arriesgada', 'El máximo que puedes ganar', 'El valor esperado exacto', 'La utilidad máxima', 'a', 'PEC satisface U(PEC) = E(U). Consumidor es indiferente entre PEC seguro vs lotería. Diferencia: PEC < E[valor] indica aversión al riesgo.', true),
('1', 'Consumo bajo Incertidumbre', 'Prima de riesgo', 'intermedio', '¿Qué mide la ''Prima de Riesgo'' en consumo bajo incertidumbre?', 'Diferencia entre valor esperado y PEC; cuánto pagarías para evitar riesgo', 'La tasa de interés', 'El pago de un seguro', 'La probabilidad del resultado', 'a', 'Prima de Riesgo = E[w] - PEC. Mide cuánta utilidad sacrificas por tomar la lotería. Mayor prima indica mayor aversión al riesgo.', true),
('1', 'Consumo bajo Incertidumbre', 'Medidas de aversión al riesgo', 'avanzado', '¿Cómo se mide la aversión al riesgo cuantitativamente?', 'Coeficiente de aversión absoluta: -U''''(w)/U''(w) o relativa: -wU''''(w)/U''(w)', 'Simplemente con U''''(w) < 0', 'Con la pendiente de demanda', 'No se puede medir', 'a', 'Arrow-Pratt: aversión absoluta = -U''''(w)/U''(w); aversión relativa = -wU''''(w)/U''(w). Mayor valor = mayor aversión. Fundamental en teoría de seguros.', true),
('1', 'Consumo bajo Incertidumbre', 'Decisión comparativa', 'intermedio', 'Opción A: $100 seguro (U=100). Opción B: 50% ganar $200 (U=140), 50% perder $100 (U=60). ¿Cuál elegir?', 'E(Uᴮ) = 0.5(140)+0.5(60) = 100 = E(Uᴬ). Indiferente', 'Opción A siempre', 'Opción B siempre', 'Depende de edad', 'a', 'En este caso específico, ambas opciones dan la misma utilidad esperada (100). Consumidor es indiferente. Pequeños cambios determinarían la preferencia.', true),
('1', 'Consumo bajo Incertidumbre', 'Teoría prospectiva', 'avanzado', '¿Qué propone la Teoría Prospectiva de Kahneman-Tversky?', 'Las personas NO maximizan utilidad esperada: son amantes del riesgo EN pérdidas, aversas EN ganancias (valor subjetivo ponderado)', 'Es lo mismo que utilidad esperada', 'Maximización es irracional', 'Los precios determinan todo', 'a', 'Prospectiva: v(ganancia) ≠ -v(-pérdida), con función de valor asimétrica. Hay aversión en ganancias, amour du risque en pérdidas. Explica paradojas empíricas de utilidad esperada.', true),
('1', 'Función de Producción', 'Definición', 'basico', '¿Qué es una función de producción y cuándo se usa?', 'Relaciona inputs (K,L) con output (Q); muestra máxima producción técnicamente posible', 'Muestra cuánto gana una empresa', 'Muestra los precios de inputs', 'Es lo mismo que función de utilidad', 'a', 'Q = f(K,L) es una relación técnica. No involucra precios, solo cantidades físicas. Fundamental en teoría de la empresa.', true),
('1', 'Función de Producción', 'Cobb-Douglas', 'basico', '¿Cuál es la forma de una función Cobb-Douglas y qué representan α y β?', 'Q = AK^α L^β; α y β son elasticidades de producción (exponentes)', 'Q = αK + βL', 'Q = min{αK, βL}', 'Q = A/(K^α + L^β)', 'a', 'Cobb-Douglas Q = AK^α L^β es la forma más usada. Exponentes = elasticidades; su suma determina rendimientos a escala.', true),
('1', 'Función de Producción', 'Función Leontief', 'basico', '¿Cuándo usas función Leontief Q = min{aK, bL}?', 'Cuando los inputs son complementos perfectos; proporciones fijas (ej: máquinas+trabajadores)', 'Cuando son sustitutos perfectos', 'Cuando no hay relación', 'Nunca en economía real', 'a', 'Leontief Q = min{aK, bL}: inputs en proporción fija. Si necesitas 2 máquinas por trabajador, más de uno sin el otro no produce más. Usado en análisis input-output.', true),
('1', 'Función de Producción', 'Evaluación de producción', 'basico', 'Con Q = 10L, K=4, L=5, ¿cuál es la producción?', 'Q = 10(5) = 50', 'Q = 10 × 4 × 5 = 200', 'Q = 10 + 4 + 5 = 19', 'Q = 10/5 = 2', 'a', 'Q = 10L es lineal en L solamente (K no aparece, probablemente fijo). Con L=5: Q = 50. Esta función tiene productividad del trabajo constante.', true),
('1', 'Función de Producción', 'Complementariedad en Leontief', 'intermedio', 'En Q = min{5K, 10L} con K=4, L=3, ¿cuál es Q y qué factor limita?', 'Q = min{20, 30} = 20. Capital limita (es el cuello de botella)', 'Q = min{20, 30} = 30. Trabajo limita', 'Q = 5K + 10L = 50', 'Q = 120', 'a', 'El factor limitante es el que produce menor valor en min{}: 5K=20 < 10L=30. Capital es insuficiente, por lo que Q=20 es el máximo.', true),
('1', 'Función de Producción', 'Productividad relativa', 'intermedio', '¿Qué función produce más: Q₁ = 10L (lineal) o Q₂ = 10√L (raíz)?', 'Depende de L: lineales dominan √ para L>1. Con L=4: Q₁=40, Q₂=20', 'Siempre √L es más productivo', 'Siempre lineal es más productivo', 'Son iguales', 'a', '10L crece más rápido que 10√L. A L=4: lineal da 40, raíz da 20. La raíz refleja rendimientos decrecientes; lineal refleja productividad constante.', true),
('1', 'Productividad Marginal y Media', 'Definiciones', 'basico', '¿Cuál es la diferencia entre Producto Marginal y Producto Medio?', 'PMg = cambio en Q por 1 unidad más de L; PMe = Q/L (total dividido cantidad)', 'PMg y PMe son lo mismo', 'PMg es en valor monetario; PMe es en unidades', 'No hay diferencia conceptual', 'a', 'PMg = ∂Q/∂L (derivada, marginal); PMe = Q/L (promedio). PMg marginal determina decisiones de contratación; PMe determina eficiencia general.', true),
('1', 'Productividad Marginal y Media', 'Relación entre PMg y PMe', 'intermedio', '¿Cuándo PMg > PMe, ¿qué sucede con PMe?', 'PMe sube (PMg la tira hacia arriba)', 'PMe baja', 'PMe no cambia', 'Depende del precio', 'a', 'Cuando marginal > promedio, el promedio sube (efecto de nuevo trabajador ''bueno''). Cuando PMg < PMe, PMe baja. Cuando PMg = PMe, PMe alcanza su máximo.', true),
('1', 'Productividad Marginal y Media', 'Cálculo con Q = 100L - 2L²', 'intermedio', 'Con Q = 100L - 2L², con L=10, calcula PMg y PMe', 'PMe = (100(10)-2(100))/10 = 80; PMg = 100-4(10) = 60', 'PMg = 100, PMe = 100', 'PMg = 60, PMe = 100', 'No se pueden calcular', 'a', 'Q(10) = 1000-200 = 800. PMe = 800/10 = 80. PMg = dQ/dL = 100-4L → PMg(10) = 60. Aquí PMg < PMe, indicando que PMe está bajando.', true),
('1', 'Productividad Marginal y Media', 'Rendimientos decrecientes', 'intermedio', '¿Cuándo entra en juego la ley de rendimientos marginales decrecientes?', 'Cuando PMg < PMe y continúa bajando (derivada de PMg es negativa)', 'Desde el inicio siempre', 'Nunca; es mito', 'Solo en largo plazo', 'a', 'Rendimientos decrecientes: ∂PMg/∂L < 0. Común en corto plazo con factores fijos. Más trabajo en fábrica fija → cada trabajador extra añade menos (menos espacio, máquinas, etc.).', true),
('1', 'Productividad Marginal y Media', 'Productividades con Cobb-Douglas', 'avanzado', 'Para Q = 10K^0.5 L^0.5 con K fijo, ¿QUÉ propiedad tiene PMgₗ?', 'PMgₗ = 5K^0.5 L^(-0.5), decreciente en L (elasticidad 0.5)', 'PMgₗ es constante', 'PMgₗ crece en L', 'No depende de K', 'a', 'Para Cobb-Douglas con exponente β en L: elasticidad de producto respecto L = β. Aquí β=0.5 → PMgₗ ∝ L^(-0.5), decreciente. Mayor L → menor PMgₗ.', true),
('1', 'Isocuantas y RMST', 'Definición de isocuanta', 'basico', '¿Qué es una isocuanta?', 'Todas las combinaciones K,L que producen el MISMO nivel Q', 'Una línea de presupuesto para la empresa', 'Una curva de demanda', 'El mismo que una curva de indiferencia', 'a', 'Isocuanta = igual cantidad. Análoga a curva de indiferencia pero para producción. Pendiente = RMST, mostrando tasa técnica de sustitución entre inputs.', true),
('1', 'Isocuantas y RMST', 'RMST', 'basico', '¿Qué mide la RMST (Relación Marginal de Sustitución Técnica)?', 'Cuántas unidades de K renuncias por 1 unidad de L, manteniendo Q constante', 'La relación de precios de inputs', 'Cuánto produce cada input', 'La ganancia de la empresa', 'a', 'RMST = PMgₗ/PMgₖ (negative pendiente de isocuanta). Indica cómo sustituir técnicamente un input por otro sin cambiar output.', true),
('1', 'Isocuantas y RMST', 'RMST Cobb-Douglas', 'intermedio', 'Para Q = 10K^0.25 L^0.25, ¿cuál es la RMST?', 'RMST = PMgₗ/PMgₖ = (2.5K^0.25 L^(-0.75))/(2.5L^0.25 K^(-0.75)) = K/L', 'RMST = 0.25/0.25 = 1', 'RMST = K × L', 'No existe', 'a', 'Simplificando el ratio: RMST = K/L. Para Cobb-Douglas Q = K^α L^β, RMST = (α/β)(K/L). Aquí α=β → RMST = K/L.', true),
('1', 'Isocuantas y RMST', 'RMST Leontief', 'intermedio', 'Para Q = min{2K, 3L}, ¿cuál es la RMST?', 'Indefinida en la esquina (2K=3L); 0 o ∞ fuera de proporción', 'RMST = 2/3', 'RMST = 3/2', 'RMST = 1', 'a', 'Complementos perfectos: en el codo hay ángulo recto (RMST indefinida). Fuera del codo: si hay exceso de K, RMST=0; si hay exceso de L, RMST=∞.', true),
('1', 'Isocuantas y RMST', 'Curvatura de isocuanta', 'intermedio', '¿Una isocuanta convexa hacia el origen (como Cobb-Douglas) qué refleja?', 'RMST decreciente: a más L, menos K sacrificas por L (sustitutos crecientes)', 'Sustitutos perfectos', 'Complementos perfectos', 'No tiene significado', 'a', 'Convexidad = RMST decreciente. Refleja eficiencia de combinar inputs: especializar mucho en uno es ineficiente, es mejor balance. Reduce costos.', true),
('1', 'Isocuantas y RMST', 'Elasticidad de sustitución', 'avanzado', '¿Qué es la elasticidad de sustitución σ?', 'Cambio % en (K/L) respecto cambio % en RMST; mide fácil sustitución', 'Cambio en precios', 'Cambio en producción', 'No existe', 'a', 'σ = %Δ(K/L) / %Δ(RMST). σ=1 (Cobb-Douglas): moderado. σ→∞ (sustitutos perfectos): fácil. σ=0 (Leontief): imposible. Crucial para teoría de distribución.', true),
('1', 'Rendimientos a Escala', 'Definición', 'basico', '¿Qué son los Rendimientos a Escala?', 'Cómo cambia Q cuando TODOS los inputs aumentan en la misma proporción', 'Cómo cambia un input cuando cambia otro', 'La relación de precios', 'Lo mismo que productividad marginal', 'a', 'Rendimientos a escala (largo plazo): Q(tK, tL) vs tQ(K,L). Si >t = crecientes; =t = constantes; <t = decrecientes.', true),
('1', 'Rendimientos a Escala', 'Cobb-Douglas y suma de exponentes', 'basico', 'Para Q = AK^α L^β, ¿cómo determines el tipo de rendimientos?', 'Por la suma α+β: >1 crecientes, =1 constantes, <1 decrecientes', 'Por A', 'Por K+L', 'No se puede determinar', 'a', 'Propiedad fundamental de Cobb-Douglas: α+β = grado de homogeneidad. Q(tK,tL) = t^(α+β) Q(K,L). Si α+β=1.5 → Q se multiplica por t^1.5 (crecientes).', true),
('1', 'Rendimientos a Escala', 'Ejemplos de clasificación', 'basico', 'Clasifica: a) Q = 5K + 10L; b) Q = 2K² + 3L²; c) Q = K^0.3 L^0.4', 'a) Constantes (1); b) Crecientes (4); c) Decrecientes (0.7)', 'Todos iguales', 'Todos decrecientes', 'No se pueden clasificar', 'a', 'a) α+β=1 (lineal); b) α+β=4 (cuadrática); c) α+β=0.7. Propiedad simple: suma de exponentes = tipo de rendimiento.', true),
('1', 'Rendimientos a Escala', 'Implicación económica', 'intermedio', '¿Qué implicaciones tienen rendimientos crecientes para la estructura industrial?', 'Economías de escala → monopolios naturales (1 empresa grande bate muchas pequeñas)', 'Muchas empresas pequeñas', 'Precios siempre bajos', 'Sin implicación', 'a', 'Rendimientos crecientes: duplicar inputs más que duplica output. El costo medio baja → una empresa grande es más eficiente. Llevó a monopolios en utilities (telefonía, electricidad).', true),
('1', 'Rendimientos a Escala', 'Test de duplicación', 'intermedio', 'Si duplicas K y L en Q = K^0.5 L^0.5, ¿cuánto aumenta Q?', 'Q se multiplica por 2^(0.5+0.5) = 2^1 = 2 (constantes)', 'Q se multiplica por 2^2 = 4', 'Q no cambia', 'Q se multiplica por 2^0.5 ≈ 1.41', 'a', 'Q(2K,2L) = (2K)^0.5(2L)^0.5 = 2^(0.5+0.5) K^0.5 L^0.5 = 2·Q. Duplicar inputs duplica output → rendimientos constantes.', true),
('1', 'Rendimientos a Escala', 'Análisis empirico', 'avanzado', 'Una empresa duplica todos los inputs y la producción sube de 100 a 250. ¿Qué tipo de rendimientos?', 'Crecientes: 250/100 = 2.5 > 2 (más que duplica)', 'Constantes', 'Decrecientes', 'No se puede determinar', 'a', 'Test simple: si duplicas inputs y Q > 2Q₀, hay rendimientos crecientes. Aquí: 250 > 200 → crecientes. Sugiere economías de escala no explotadas.', true);

